import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'my-app';
  todoArray:Array<String>=[] ;
  addTodo(value:String){  
    this.todoArray.push(value)  
    console.log(this.todoArray)  } 
}
<div class="container"> 
  <form>  
    <div class="form-group">  
      <h1 class="text-center text-primary">Todo App</h1>   
      <div class="input-group-prepend">       
        <input type="text" #todo class="form-control" placeholder="Add Todo" name="todo"> 
        <span class="input-group-text" (click)="addTodo(todo.value)"><i class="material-icons">add</i></span>
      </div>  
    </div> 
  </form>
  <div class="data">  <ul class="list-instyled">   <li *ngFor="let todo of todoArray">{{todo}}<i class="material-icons">delete</i></li>  </ul>  </div>
</div>
